        <?php
        $queryCheckEmpty = "SELECT * FROM daily_list WHERE user_id = $user_id";
        $result = mysqli_query($conn,$queryCheckEmpty);
        $isFill = mysqli_fetch_array($result);
        // var_dump($result);
        if(empty($isFill["activity_name"])) :?>
            <p>-- No Activities --</p>
        <?php else :?>
        <div class="table"></div>
        <table border="1" cellspacing="0" cellpadding="10" id="display-list">
            <tr>
                <th>No</th>
                <th>Status</th>
                <th>Activity Name</th>
                <th>Date</th>
                <th>Category</th>
                <th>Modify</th>
            </tr>
            <?php 
                $i = 1;
                $query = "SELECT * FROM daily_list WHERE user_id = $user_id";
                $daily_list = showDailyList($query);
                // var_dump($daily_list);
            ?>
            <?php foreach($daily_list as $data): ?>
            <tr>
                <td><center><?= $i ?></center></td>
                <td><center><?= $data["status"]?></center></td>
                <td><center><?= $data["activity_name"]?></center></td>
                <td><center><?= $data["date"]?></center></td>
                <td><center><?= $data["category"]?></center></td>
                <td><center><a href="edit.php?id=<?= $data["id"];?>" class="edit" onclick="return confirm('Are you sure ?');">Edit</a> 
                | <a href="delete.php?id=<?= $data["id"];?>" class="delete" onclick="return confirm('Are you sure ?');">Delete</a></center></td>
            </tr>
            <?php $i++ ?>
            <?php endforeach; ?>
        </table>
        <?php endif; ?>
